        private IEnumerable<string> GetTimesOfDayEncounters()
        {
            var dayEncounters = GetCollection(EnvironmentConstants.TimesOfDay.Day);
            var nightEncounters = GetCollection(EnvironmentConstants.TimesOfDay.Night);

            return dayEncounters.Union(nightEncounters);
        }

