        public void CharactersAreAMinorityEncounter(string environment, string temperature, string timeOfDay, int level)
        {
            var percentage = GetCharacterEncounterPercentage(environment, temperature, timeOfDay, level);
            Assert.That(percentage, Is.LessThan(.5));
            Console.WriteLine("Actual percentage was {0:P}", percentage);
        }

        private double GetCharacterEncounterPercentage(string environment, string temperature, string timeOfDay, int level)
        {
            var characters = CollectionSelector.SelectFrom(TableNameConstants.CreatureGroups, CreatureConstants.Character);
            var undeadNPC = CollectionSelector.SelectFrom(TableNameConstants.CreatureGroups, GroupConstants.UndeadNPC);

            characters = characters.Union(undeadNPC);

            var encounters = EncounterCollectionSelector.SelectAllWeightedFrom(level, environment, temperature, timeOfDay);
            var creatures = encounters.Select(e => e.First().Key).Select(c => GetCreature(c));

            var characterEncounters = creatures.Where(cr => characters.Contains(cr));
            var percentage = characterEncounters.Count() / (double)creatures.Count();

            //INFO: These assertions verify that we are working with a valid, weighted collection
            Assert.That(encounters, Is.Not.Unique);
            Assert.That(creatures, Is.Not.Unique);
            Assert.That(characterEncounters, Is.Empty.Or.Not.Unique);

            return percentage;
        }

