        public void DragonsAreAMinorityEncounter(string environment, string temperature, string timeOfDay, int level)
        {
            var percentage = GetDragonEncounterPercentage(environment, temperature, timeOfDay, level);
            Assert.That(percentage, Is.LessThan(.5));
            Console.WriteLine("Actual percentage was {0:P}", percentage);
        }

        private double GetDragonEncounterPercentage(string environment, string temperature, string timeOfDay, int level)
        {
            var dragons = CollectionSelector.SelectFrom(TableNameConstants.CreatureGroups, GroupConstants.Dragon);
            var encounters = EncounterCollectionSelector.SelectAllWeightedFrom(level, environment, temperature, timeOfDay);
            var creatures = encounters.Select(e => e.First().Key).Select(c => GetCreature(c));

            var dragonEncounters = creatures.Where(cr => dragons.Contains(cr));
            var percentage = dragonEncounters.Count() / (double)creatures.Count();

            //INFO: These assertions verify that we are working with a valid, weighted collection.
            Assert.That(encounters, Is.Not.Unique);
            Assert.That(creatures, Is.Not.Unique);

            return percentage;
        }

