        public void DragonsAndCharactersAreAMajorityEncounter(string environment, string temperature, string timeOfDay, int level)
        {
            var dragonPercentage = GetDragonEncounterPercentage(environment, temperature, timeOfDay, level);
            var characterPercentage = GetCharacterEncounterPercentage(environment, temperature, timeOfDay, level);

            var totalPercentage = dragonPercentage + characterPercentage;
            Assert.That(totalPercentage, Is.GreaterThan(.5));
            Console.WriteLine("Actual percentage was {0:P}", totalPercentage);
        }

